const redux=require("redux"),qlcv=(state={showForm:!1,data:[],statusLoadData:!0,dataEntity:{},isEdit:!1},action)=>{switch(action.type){case"addData":return{...state,data:[...state.data,...action.data]};case"loadData":return{...state,statusLoadData:action.statusLoadData,data:action.data||state.data};case"editShowForm":return{...state,showForm:action.showForm||!state.showForm,isEdit:!1};case"editEntity":return{...state,dataEntity:action.data,showForm:!0,isEdit:!0};default:return state}},store=redux.createStore(qlcv);store.subscribe(state=>{console.log(store.getState())});export default store;